mapper.select:
SELECT s.* from student s


        name = #{name}


        and degree = #{degree}


        and clazzId = #{clazzId}

mapper.update:
update student
name = #{name}, no = #{no}, gender = #{gender}, phone = #{phone}, degree = #{degree}, id_card = #{idCard}, is_college = #{isCollege}, address = #{address}, graduation_date = #{graduationDate}, violation_count = #{violationCount}, violation_score = #{violationScore}, clazz_id = #{clazzId}, update_time = now()
where id = #{id}

mapper.delete:
delete from student where in
//item指的是循环变量的名称
    #{id}
